firestore.rules:
rules_version = '2';
service cloud.firestore {
  match /databases/{database}/documents {
    // Users can only read their own profile, but can be read by any authenticated user.
    match /users/{userId} {
      allow get: if request.auth != null;
      allow list: if request.auth != null;
      allow write: if request.auth.uid == userId;
    }

    // Users can only read/write their own contacts
    match /users/{userId}/contacts/{contactId} {
       allow read, write: if request.auth.uid == userId;
    }

    // Participants of a chat can read/write to the chat document.
    match /chats/{chatId} {
        allow read, write: if request.auth.uid in resource.data.participants;
    }

    // Participants of a chat can read/write messages in that chat's subcollection.
    match /chats/{chatId}/messages/{messageId} {
      allow read, write: if get(/databases/$(database)/documents/chats/$(chatId)).data.participants[request.auth.uid] == true;
    }
  }
}
